var _ctx=document.querySelector('meta[name="_ctx"]').content;token=$("meta[name='_csrf']").attr("content");header=$("meta[name='_csrf_header']").attr("content");var onloadCallback=function(){grecaptcha.render('html_element',{'sitekey':keyG,'callback':myRecaptchaCallBack})};function myRecaptchaCallBack(value){var xhr=new XMLHttpRequest();xhr.open("POST",_ctx+"validation/recaptcha-v2",!0);xhr.setRequestHeader("content-type","application/x-www-form-urlencoded; charset=UTF-8");xhr.setRequestHeader(header,token);xhr.onreadystatechange=function(){if(xhr.readyState==4&&xhr.status==200){captchaError=document.getElementById('hiddenRecaptcha-error');if(captchaError==null){}else{captchaError.remove()}}};xhr.send("response="+value);return!0}
function refrescarCaptcha(){var xhr=new XMLHttpRequest();xhr.open("GET",_ctx+'imagen-captcha/nueva',!0);xhr.setRequestHeader("content-type","application/x-www-form-urlencoded; charset=UTF-8");xhr.setRequestHeader(header,token);xhr.onreadystatechange=function(){if(xhr.readyState==4&&xhr.status==200){document.getElementById('captchaCode').value = ""; document.querySelector('#ImgCaptcha').setAttribute('src',"data:image/png;base64,"+xhr.responseText.substr(1,xhr.responseText.length-2))}};xhr.send();return!0}function checkUsername(){document.querySelector('#username').value = document.getElementById('usernamed').value+""+document.querySelector('#captchaCode').value;}